mapper.select:
		select a.LOG_TIME,a.MODULE,a.OP_TYPE,a.USER_ID,a.IP,a.PARAM,a.IS_ERR,
		to_char(a.LOG_TIME,'yyyy-MM-dd hh24:mi:ss') AS LOG_TIME_STR,b.LOGIN_NAME,b.REAL_NAME,b.LINK_PHONE
		FROM cm_log a LEFT JOIN sys_user_def b
		ON a.user_id = b.user_id
		where 1=1
		
			AND LOG_TIME > '${START_TIME}'
		
		
			AND LOG_TIME < '${END_TIME}'
		
		
			AND MODULE = #{MODULE}
		
		
			AND OP_TYPE = #{OP_TYPE}
		
		
			AND IS_ERR = #{IS_ERR}
		
		order by LOG_TIME desc
		
			LIMIT ${START_POS},${PAGE_SIZE}
