mapper.update:
update busi_app_mail_msg
M_TITLE=#{M_TITLE}, M_CONTENT=#{M_CONTENT}, STS=#{STS}, IS_OPEN=#{IS_OPEN}, LINK_MAIL=#{LINK_MAIL}, AUDIT_USER=#{AUDIT_USER},AUDIT_USER_NAME=#{AUDIT_USER_NAME},AUDIT_TIME=now(), AUDIT_OPINION=#{AUDIT_OPINION}, REPLY_USER=#{REPLY_USER},REPLY_USER_NAME=#{REPLY_USER_NAME},REPLY_TIME=now(), REPLY_CONTENT=#{REPLY_CONTENT},
WHERE MSG_PK = #{MSG_PK}

mapper.select:
select  count(*) as TOTAL
from busi_app_mail_msg a
where 1=1

	AND MSG_PK = #{MSG_PK}


	AND SITE_PK = #{SITE_PK}


	AND BOX_NAME = #{BOX_NAME}


	AND STS = #{STS}


	AND (M_TITLE like '%${QUERY_NAME}%' or M_CONTENT like '%${QUERY_NAME}%'
	)


	AND LINK_MAN like '%${LINK_MAN}%'


	AND LINK_PHONE like '%${LINK_PHONE}%'


	AND IS_OPEN = #{IS_OPEN}


	AND AUDIT_USER_NAME like '%${AUDIT_USER_NAME}%'


	AND REPLY_USER_NAME like '%${REPLY_USER_NAME}%'

order by CREATE_TIME desc

select MSG_PK,SITE_PK,BOX_NAME,M_TITLE,M_CONTENT, LINK_MAN,LINK_PHONE,STS,IS_OPEN,REPLY_CONTENT, AUDIT_USER,AUDIT_TIME,AUDIT_OPINION,REPLY_USER,REPLY_TIME, AUDIT_USER_NAME,REPLY_USER_NAME,CREATE_TIME,LINK_MAIL, DATE_FORMAT(CREATE_TIME, '%Y-%m-%d %H:%i:%s') AS CREATE_TIME_STR, DATE_FORMAT(AUDIT_TIME, '%Y-%m-%d %H:%i:%s') AS AUDIT_TIME_STR, DATE_FORMAT(REPLY_TIME, '%Y-%m-%d %H:%i:%s') AS REPLY_TIME_STR, REG,DEPT,POSTCODE,ADDR,SEX,AGE from busi_app_mail_msg a where 1=1
AND MSG_PK = #{MSG_PK}
AND SITE_PK IN(SELECT SITE_PK FROM busi_site_def WHERE site_code=#{SITE_CODE})
AND BOX_NAME = #{BOX_NAME}
AND STS = #{STS}
AND (M_TITLE like '%${QUERY_NAME}%' or M_CONTENT like '%${QUERY_NAME}%' )
AND LINK_MAN like '%${LINK_MAN}%'
AND LINK_PHONE like '%${LINK_PHONE}%'
AND IS_OPEN = #{IS_OPEN}
AND AUDIT_USER_NAME like '%${AUDIT_USER_NAME}%'
AND REPLY_USER_NAME like '%${REPLY_USER_NAME}%'
order by CREATE_TIME desc
LIMIT ${START_POS},${PAGE_SIZE}

select  MSG_PK,SITE_PK,BOX_NAME,M_TITLE,
	LINK_MAN,LINK_PHONE,STS,IS_OPEN,LINK_MAIL,
	REPLY_USER,REPLY_TIME,
	DATE_FORMAT(CREATE_TIME, '%Y-%m-%d %H:%i:%s') AS CREATE_TIME_STR,
	DATE_FORMAT(AUDIT_TIME, '%Y-%m-%d %H:%i:%s') AS AUDIT_TIME_STR,
	DATE_FORMAT(REPLY_TIME, '%Y-%m-%d %H:%i:%s') AS REPLY_TIME_STR,
	REG,DEPT,POSTCODE,ADDR,SEX,AGE
from busi_app_mail_msg a
where 1=1

	AND MSG_PK = #{MSG_PK}


	AND SITE_PK = #{SITE_PK}


	AND SITE_PK IN(SELECT SITE_PK FROM busi_site_def WHERE site_code=#{SITE_CODE})


	AND BOX_NAME = #{BOX_NAME}


	AND STS = #{STS}


	AND (M_TITLE like '%${QUERY_NAME}%' or M_CONTENT like '%${QUERY_NAME}%'
	)


	AND LINK_MAN like '%${LINK_MAN}%'


	AND LINK_PHONE like '%${LINK_PHONE}%'


	AND IS_OPEN = #{IS_OPEN}


	AND AUDIT_USER_NAME like '%${AUDIT_USER_NAME}%'


	AND REPLY_USER_NAME like '%${REPLY_USER_NAME}%'

order by CREATE_TIME desc

	LIMIT ${START_POS},${PAGE_SIZE}
